mapper.select:
		SELECT rnum, identifier, subject, context
        FROM (
            SELECT rnum, identifier, subject, context, ROW_NUMBER() OVER (ORDER BY rnum) AS rn
            FROM (
                SELECT ROWNUM AS rnum, T1.identifier, T1.subject, T1.context
                FROM (
                    SELECT *
                    FROM termsofuse
                ) T1
            )
            WHERE ROWNUM <= #{pageSize} * (#{pageNo} - 1) + #{pageSize}
        )
        WHERE rn BETWEEN (#{pageSize} * (#{pageNo} - 1)) + 1 AND #{pageSize} * #{pageNo}
